const bgClasses = [
  "text-red",
  "text-pink",
  "text-yellow",
  "text-green",
  "text-cyan",
];

export default function BottamPoster() {
  // const { ref, inView } = useInView({
  //   triggerOnce: false,
  //   threshold: 0.4,
  //   delay: 5,
  // });

  const { ref: heroRef, inView: heroInView } = useInView({
    threshold: 0.05,
  });

  const [bgIndex, setBgIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setBgIndex((prev) => (prev + 1) % bgClasses.length);
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <motion.div
        ref={heroRef}
        className="w-full overflow-hidden whitespace-nowrap"
      >
        {heroInView && (
          <motion.span
            className={`font-anton inline-block text-[45vw] leading-none ${bgClasses[bgIndex]} sm:text-[48vw]`}
            transition={{
              duration: 10,
              ease: "linear",
              repeat: Infinity,
            }}
            initial={{ translateX: 0 }}
            animate={heroInView ? { x: ["0%", "-50%"] } : false}
          >
            Let’s create Together!&nbsp;&nbsp;Let’s create Together!&nbsp;&nbsp;
          </motion.span>
        )}
      </motion.div>

      {/* <div className="relative z-30 w-full overflow-hidden">
        <motion.div
          ref={ref}
          initial={{ height: "100%" }}
          animate={{ height: inView ? "0%" : "100%" }}
          transition={{ duration: 0.7, ease: "easeInOut" }}
          className="absolute top-0 left-0 hidden w-full bg-black md:block"
          style={{ zIndex: 60 }}
        />

        <div className="relative grid grid-cols-1 items-center justify-center gap-1 md:grid-cols-5">
          {data.map((item) => (
            <Card item={item} key={item.name} />
          ))}
        </div>
      </div> */}
    </>
  );
}

